import Foundation
import XCTest

@testable import HaishinKit

final class FilenameUtilTest: XCTestCase {
    func testMain() {
        print(FILENAME_MAX)
        XCTAssertEqual("bar", FilenameUtil.fileName(resourceName: "/foo/bar?hogehoge"))
        XCTAssertEqual("hoge", FilenameUtil.fileName(resourceName: "hoge?foobar"))
        let test = FilenameUtil.fileName(resourceName: "sdfjhkfl.khjdsflk-jhsjkf...sdfkhkj")
        print("punctuation/spacial chars removed", test)
        XCTAssertEqual(
            "",
            FilenameUtil.fileName(resourceName: "")
        )
    }
}
